// Função para sortear a carta

function Sortearcarta() {
  zerarCarta("carta-jogador");
  zerarCarta("carta-computador");
  resultado.innerHTML = "";
  var i = cartas.length;
  var cartaSorteadaComputador = Math.floor(Math.random() * i);
  cartaComputador = cartas[cartaSorteadaComputador];
  var cartaSorteadaJogador = Math.floor(Math.random() * i);

  while (cartaSorteadaJogador == cartaSorteadaComputador) {
    var cartaSorteadaJogador = Math.floor(Math.random() * i);
  }
  cartaJogador = cartas[cartaSorteadaJogador];
  document.getElementById("btnJogar").disabled = false;
  document.getElementById("btnSortear").disabled = true;

  exibirCartaJogador();
}

// Função para extrair o atributo selecionado

function atributoEscolhido() {
  var selecionado = document.getElementsByName("atributo");

  for (var i = 0; i < selecionado.length; i++) {
    if (selecionado[i].checked == true) {
      return selecionado[i].value;
    }
  }
}

// Função para jogar

function jogar() {
  var atributoMarcado = atributoEscolhido();
  var valorCartaJogador = cartaJogador.atributos[atributoMarcado];
  var valorCartaComputador = cartaComputador.atributos[atributoMarcado];
  var pontosJogador = 0;
  var pontosComputador = 0;

  while (isNaN(atributoMarcado)) {
    if (valorCartaJogador > valorCartaComputador) {
      placar[0]++;
      htmlResultado = `<p class='resultado-final'>Parabéns! Você venceu! ⚔️</p><br><img src="https://64.media.tumblr.com/2a2df6f48b1072558ba7388586624c40/tumblr_inline_pj4mzdHNjy1rict0a_540.gifv">'`;
      break;
    } else if (valorCartaJogador < valorCartaComputador) {
      placar[1]++;
      htmlResultado = `<p class='resultado-final'> Ops! Você perdeu! Tente novamente!</p><br><img src="https://c.tenor.com/mksT8jk3cNMAAAAC/ugh-mermista.gif">'`;
      break;
    } else if (isNaN(valorCartaJogador) || isNaN(valorCartaComputador)) {
      htmlResultado = `<p class='resultado-final'> Por favor, selecione um atributo!</p><br><img src="https://c.tenor.com/LKcJO3TmRtsAAAAM/shera-adora.gif">'`;
      resultado.innerHTML = htmlResultado;
      return;
    } else {
      placar[0]++;
      placar[1]++;
      htmlResultado = `<p class='resultado-final'> Empate! 🛡️</p><br><img src="https://i.pinimg.com/originals/d2/28/f3/d228f316b83894cf86cb73b394d4a509.gif">'`;
      break;
    }
  }
  resultado.innerHTML = htmlResultado;
  document.getElementById("btnJogar").disabled = true;
  document.getElementById("btnSortear").disabled = false;
  exibirCartaComputador();
  exibirPlacar();
}

// Função para mostrar a imagem do jogador

function exibirCartaJogador() {
  var cartaJogadorImagem = document.getElementById("carta-jogador");
  cartaJogadorImagem.style.backgroundImage = `url(${cartaJogador.imagem})`;
  var moldura =
    '<img src="https://i.pinimg.com/originals/4e/49/06/4e490632804acd7d22a85d0bcf3e319e.png" style=" width: inherit; height: inherit; position: absolute;">';
  var tagHTML = `<div id=opcoes class=carta-status>`;
  var texto = "";
  for (var atributo in cartaJogador.atributos) {
    texto +=
      "&nbsp;<input type='radio' name='atributo' value='" +
      atributo +
      "'>&nbsp;" +
      "" +
      atributo +
      " " +
      ": " +
      cartaJogador.atributos[atributo] +
      "<br>";
    var nome = `<p class=carta-subtitle>${cartaJogador.nome}</p>`;
    var frase = `<p class=carta-subtitle2>${cartaJogador.fala}</p>`;
    cartaJogadorImagem.innerHTML =
      moldura + nome + frase + tagHTML + texto + `</div>`;
  }
}

function exibirCartaComputador() {
  var cartaComputadorImagem = document.getElementById("carta-computador");
  cartaComputadorImagem.style.backgroundImage = `url(${cartaComputador.imagem})`;
  var moldura =
    '<img src="https://i.pinimg.com/originals/4e/49/06/4e490632804acd7d22a85d0bcf3e319e.png" style=" width: inherit; height: inherit; position: absolute;">';
  var tagHTML = `<div id=opcoes class=carta-status2>`;
  var texto = "";
  for (var atributo in cartaComputador.atributos) {
    texto +=
      "<p type='text' name='atributo' value='" +
      atributo +
      "'>" +
      "&nbsp;&nbsp;" +
      atributo +
      " " +
      ": " +
      cartaComputador.atributos[atributo];
    var nome = `<p class=carta-subtitle>${cartaComputador.nome}</p>`;
    var frase = `<p class=carta-subtitle2>${cartaComputador.fala}</p>`;
    cartaComputadorImagem.innerHTML =
      moldura + nome + frase + tagHTML + texto + `</div>`;
  }
}

// Função para zerar cartas da tela

function zerarCarta(id) {
  var idCarta = document.getElementById(id);
  var idCartaTexto =
    '<img src="https://cinemaplanet.pt/wp-content/uploads/2020/06/sheraposter.jpg" style=" width: inherit; height: inherit; position: absolute;">';
  idCarta.innerHTML = idCartaTexto;
}

// Função para zerar placar

function Zerar() {
  placar[0] = 0;
  placar[1] = 0;
  exibirPlacar();
}

// Função para exibir placar

function exibirPlacar() {
  var placarJogador = document.getElementById("placarJogador");
  var placarComputador = document.getElementById("placarComputador");

  placarJogador.innerHTML = `👑 Jogador: ${placar[0]}`;
  placarComputador.innerHTML = `👾 Computador: ${placar[1]}`;
}